mapper.select:
    select * from (select rownum rnum ,rr.* from(select rb.rb_num, rb.m_id,rb.rb_title,rb.rb_content,rb_stars,rb_saveimg,rb.rb_date,g.g_name,c.c_colorname,sz.sz_sizename,rp.rp_content from reviewboard rb left outer join reviewreply rp on rb.rb_num= rp.rb_num, orders o,orderlist ol, gcs gcs,goods g,color c,sizes sz where rb.m_id = o.m_id and o.o_num=ol.o_num and ol.gcs_num=gcs.gcs_num and gcs.g_num =g.g_num 
and gcs.c_subnum = c.c_subnum and gcs.sz_ssubnum = sz.sz_ssubnum and rb.m_id= #{m_id} order by rb.rb_date desc) 
rr)   where rnum >=#{startRow} and rnum <= #{endRow}
